using namespace std;

int n;
int d1[N], d2[N], d3[N];
int qs[3];
vector<int> a[N];
set<int> leaf;

void dfs(int cur, int depth, int d[]) {
	if (a[cur].size() == 1) {
		leaf.insert(cur);
	}
	for (auto nn : a[cur]) {
		if (d[nn] > depth + 1) {
			d[nn] = depth + 1;
			dfs(nn, depth + 1, d);
		}
	}
}

void input() {
	cin >> n;
	for (int i = 0; i < n - 1; i++) {
		int l, r;
		cin >> l >> r;
		l--;
		r--;
		a[l].push_back(r);
		a[r].push_back(l);
	}
	for (int i = 0; i < 3; i++) {
		cin >> qs[i];
		qs[i]--;
	}
}

int solve() {
	input();
	fill_n(d1, n, MAX);
	fill_n(d2, n, MAX);
	fill_n(d3, n, MAX);
	d1[qs[0]] = 0;
	dfs(qs[0], 0, d1);
	d2[qs[1]] = 0;
	dfs(qs[1], 0, d2);
	d3[qs[2]] = 0;
	dfs(qs[2], 0, d3);
	for (auto q : leaf) {
		if (d1[q] < d2[q] && d1[q] < d3[q]) {
			cout << "YES\n";
			return 0;
		}
	}
	cout << "NO\n";
	return 0;
}

int main() {
	FAST;
	solve();

	return 0;
}
```

